
-- Alarm Catalog
CREATE TABLE alarm_catalog (
  id INT NOT NULL,
  system VARCHAR(45) NULL,
  filter VARCHAR(45) NULL,
  manager VARCHAR(45) NULL,
  oid VARCHAR(100) NOT NULL,
  text_alarm VARCHAR(100) NOT NULL,
  severity VARCHAR(45) NULL,
  criticality VARCHAR(45) NOT NULL,
  correlate VARCHAR(100) NULL,
  PRIMARY KEY (`id`));
  
